import { ControlCameraOutlined } from '@material-ui/icons'
import { createRoot } from "react-dom/client";
import {
  createBrowserRouter,
  RouterProvider,
  Route,
  Link,
} from "react-router-dom";
import React,{useState, useEffect} from 'react'
import Cart from './components/cart/Cart'
import Navbar from './components/Nabar/Navbar'
import Products from './components/Products/Products'
import { commerce } from './lib/commerce'


const App = () => {
const [products, setProducts ] = useState([]);
const [cart, setCart] = useState({});
const [del, setDel] = useState([]);
const fetchproducts = async ()=>{
  const {data} = await commerce.products.list();
  setProducts(data);
}



const fetchcart = async ()=>{
  setCart( await commerce.cart.retrieve());
}
const handleAddToCart = async(productId,quantity)=>{
    setCart(await commerce.cart.add(productId, quantity));
}
const Emptycart = async ()=>{
  setDel(await commerce.cart.empty());
  // window.location.reload(); //It can also be used instead of fetchcart function
  fetchcart(); //another method
}
const Removecart = async () => {
  setDel(await commerce.cart.remove());
}
  useEffect(()=>{
    fetchproducts();
    fetchcart();
  },[]);  
  // commerce.cart.empty();
  console.log(cart);
const router = createBrowserRouter([
  {
    path: "/",
    element: (
      <div>
        <Navbar items ={cart.total_items}/>
        <Products products ={products} onAddToCart ={handleAddToCart}/>
        
      </div>
    ),
  },
  {
    path: "cart",
    element: <div>
      <Navbar items ={cart.total_items}/>
       <Cart cart = {cart} Emptycart ={Emptycart} Removecart={Removecart}/>
    </div>,
  },
]);

createRoot(document.getElementById("root")).render(
  <RouterProvider router={router} />
);
}
export default App;
