import React from 'react'
import { Container, Typography, Button, Grid } from '@material-ui/core'
const Cart = ({cart}) => {
    const isEmpty = !cart.line_items.length;

    const EmptyCart =()=>(
        <Typography variant='subtitle1' >You have no items in the cart</Typography>
        );
    const FilledCart=()=>(
        <>
        <Grid container spacing={3}>
        {cart.line_items.map((item)=>(
            <Grid item xs={12} sm={4} key = {item.id} >
                <div>{item.name}</div>
            </Grid>
        ))}

        </Grid>
        <div className={classes.CartDetails}>
            <Typography variant='h4'>
                subtotal:{cart.subtotal.formatted_width_symbol}
            </Typography>
            <div>
                <Button className={classes.emptyButton} size='large' type='button' variant='contained'></Button>
            </div>
        </div>
        </>
    );
  return (
    <Container>
        <div className={classes.toolbar}/>
        <Typography className={classes.title} varint="h3" >Your Shopping Cart</Typography>
        {isEmpty? <EmptyCart/>:<FilledCart/>}
    </Container>
  )
}

export default Cart
