import React from 'react'
import { Link } from 'react-router-dom'
import { Button, Typography, Divider } from '@material-ui/core'
import {Elements, CardElement, ELementsConsumer, ElementsConsumer} from "@stripe/react-stripe-js";
import { loadStripe } from '@stripe/stripe-js';
import Review from './Review';
import { PaymentElement } from '@stripe/react-stripe-js';
const PaymentForm = ({previousStep, CheckoutToken}) => {
  console.log(CheckoutToken);
const stripePromise = loadStripe('pk_test_51MkBdbSHeix4QwWnK2WQHPeB9QWq3wqDLo4agxmWXvfOZnfqAp5Wk5edvJLLznqc5rmFj8pBQYUZcgKPm1aK6rl300PXtcjkaY');
  return (
    <>
      
      <Review CheckoutToken={CheckoutToken} />  
      <Divider/>
      <Typography variant='h6' gutterBottom style={{margin:'20px 0'}} >Payment method </Typography>
      <Elements stripe={stripePromise}>
          <ElementsConsumer>
            {({elements, stripe})=>(
          <form>
            <CardElement/>
            <br/> <br/>
            <div style={{display:'flex', justifyContent:'space-between'}}>
                <Button variant='outlined'>Back</Button>
                <Button variant='contained' type='submit'>Submit</Button>
            </div>
          </form>
)}
          </ElementsConsumer>
      </Elements>
    </>
  )
}

export default PaymentForm
